/**
 * Definition for a binary tree node.
 * public class TreeNode {
 *     int val;
 *     TreeNode left;
 *     TreeNode right;
 *     TreeNode() {}
 *     TreeNode(int val) { this.val = val; }
 *     TreeNode(int val, TreeNode left, TreeNode right) {
 *         this.val = val;
 *         this.left = left;
 *         this.right = right;
 *     }
 * }
 */
class Solution {
    public int amountOfTime(TreeNode root, int start) {
        Map<Integer, TreeNode> parentMap = new HashMap<>();
        assignParent(root, null, parentMap);
        TreeNode startNode = findNode(root, start, parentMap);
        return findMinimumTimeByBfs(startNode, parentMap);
    }

    public void assignParent(TreeNode child, TreeNode parent, Map<Integer, TreeNode> parentMap) {
        if (child != null) {
            parentMap.put(child.val, parent);
            assignParent(child.left, child, parentMap);
            assignParent(child.right, child, parentMap);
        }
    }

    public TreeNode findNode(TreeNode root, int nodeToFind, Map<Integer, TreeNode> parentMap) {
        if (root.val == nodeToFind) return root;
        TreeNode parent = parentMap.get(nodeToFind);
        if (parent.left != null && parent.left.val == nodeToFind)
            return parent.left;
        return parent.right;
    }

    public int findMinimumTimeByBfs(TreeNode startNode, Map<Integer, TreeNode> parentMap) {
        Set<TreeNode> visitedNodes = new HashSet<>();
        Queue<TreeNode> bfsQueue = new ArrayDeque(parentMap.size());
        int minimumTime = 0;
        bfsQueue.add(startNode);
        while (!bfsQueue.isEmpty()) {
            int size = bfsQueue.size();
            for (int i = 0; i < size; i++) {
                TreeNode node = bfsQueue.poll();
                visitedNodes.add(node);
                if (node.left != null && !visitedNodes.contains(node.left))
                    bfsQueue.add(node.left);
                if (node.right != null && !visitedNodes.contains(node.right))
                    bfsQueue.add(node.right);
                TreeNode parent = parentMap.get(node.val);
                if (parent != null && !visitedNodes.contains(parent))
                    bfsQueue.add(parent);
            }
            minimumTime++;
        }
        return minimumTime - 1;
    }
}
