class Solution {
    int time = 0, startDist = 0;
    public int amountOfTime(TreeNode root, int start) {
        helper(root, start);
        return time;
    }

    int helper(TreeNode root, int start) {
        if (root == null) {
            return 0;
        }
        int left = helper(root.left, start);
        int right = helper(root.right, start);

        //The longest path could be the child chain of start node
        if (root.val == start) {
            time = Math.max(time, Math.max(left, right));
            root.val = -root.val;
        }
        // Or the longest path (path = distance to start + longest path of another children chain) of any start node's parent
        else if (root.left != null && root.left.val < 0) {
            startDist++;
            time = Math.max(time, right + startDist);
            root.val = -root.val;            
        } else if (root.right != null && root.right.val < 0) {
            startDist++;
            time = Math.max(time, left + startDist);
            root.val = -root.val;
        }
        return Math.max(left, right) + 1;
    }
}
