class Solution {
private:
    void traverse(TreeNode* root, vector<int> graph[]){
        if(root==NULL) return;
        if(root->left){
            graph[root->val].push_back(root->left->val);
            graph[root->left->val].push_back(root->val);
        }
        if(root->right){
            graph[root->val].push_back(root->right->val);
            graph[root->right->val].push_back(root->val);
        }
        traverse(root->left, graph);
        traverse(root->right, graph);
    }

    void bfs(int v, vector<int> graph[], int& ans){
        queue<pair<int, int>> q;
		//Second part of queue is time to infect current element
        q.push({v, ans});
        bool* visited = new bool[100001];
        for(int i=0;i<100001;i++) visited[i]=0;
        visited[v]=1;
        while(!q.empty()){
            auto tmp=q.front();
            q.pop();
            bool flag=0;
            for(int x:graph[tmp.first]){
                if(!visited[x]){
                    q.push({x, tmp.second+1});
                    visited[x]=1;
                    flag=1;
                }
            }
            if(flag) ans=max(ans, tmp.second+1);
        }
    }
public:
    int amountOfTime(TreeNode* root, int start) {
        vector<int> graph[100001];
		// traversing the tree for graph
        traverse(root, graph);
        int ans=0;
        bfs(start, graph, ans);
        return ans;
    }
};
