class Solution {
public:
    struct InfectionData {
        int height;
        int dist_to_start;
        int max_dist_to_start;
    };

    InfectionData InfectionHelper(TreeNode* root, int start) {
        if (root == nullptr) {
            return InfectionData{0, -1, -1};
        }
        InfectionData left_data = InfectionHelper(root->left, start);
        InfectionData right_data = InfectionHelper(root->right, start);
        int height = 1 + max(left_data.height, right_data.height);
        int dist_to_start = -1;
        int max_dist_to_start = -1;
        if (root->val == start) {
            dist_to_start = 0;
            max_dist_to_start = height - 1;
        }
        else if (left_data.dist_to_start != -1) {
            dist_to_start = left_data.dist_to_start + 1;
            max_dist_to_start = max({left_data.max_dist_to_start, dist_to_start + right_data.height});
        } else if (right_data.dist_to_start != -1) {
            dist_to_start = right_data.dist_to_start + 1;
            max_dist_to_start = max({right_data.max_dist_to_start, dist_to_start + left_data.height});
        }
        return InfectionData{height, dist_to_start, max_dist_to_start};
    }

    int amountOfTime(TreeNode* root, int start) {
        return InfectionHelper(root, start).max_dist_to_start;
    }
};
