class Solution {
public:
    // Create undirected graph
    void createGraph (TreeNode* root) {
        queue<TreeNode*> que;

        if (!root) return;

        que.push(root);

        while (!que.empty()) {
            int size = que.size();

            for (int i = 0; i < size; i++) {
                TreeNode* cur = que.front();
                que.pop();

                if (cur->left) {
                    que.push(cur->left);

                    adj[cur->val].push_back(cur->left->val);
                    adj[cur->left->val].push_back(cur->val);
                }

                if (cur->right) {
                    que.push(cur->right);

                    adj[cur->val].push_back(cur->right->val);
                    adj[cur->right->val].push_back(cur->val);
                }
            }
        }
    }

    int amountOfTime(TreeNode* root, int start) {
        createGraph (root);

        queue<int> que;
        que.push(start);
        visited[start] = true;
        int count = 0;

        // bfs
        while (!que.empty()) {
            int size = que.size();
            ++count;

            for (int i = 0; i < size; i++) {
                int cur =  que.front();
                que.pop();

                for (auto node : adj[cur]) {
                    if (!visited[node]) { // hasn't been visited before
                        visited[node] = true;
                        que.push(node);
                    }                  
                }
            }
        }

        return count - 1;
    }

private:
    unordered_map<int, vector<int>> adj; // <node, <adj nodes>>
    unordered_map<int, bool> visited; // <node, whether visited>
};
