public int amountOfTime(TreeNode root, int start){
    HashMap<Integer, HashSet<Integer>> map = new HashMap<>();  // to store adjacent nodes of each node
    inorder(root, -1, map);  //convert tree to graph
    Queue<Integer> queue = new LinkedList<>(); 
    queue.add(start);
    int result = 0;
    HashSet<Integer> visited = new HashSet<>();
    visited.add(start);

    // bfs to find the ans
    while(!queue.isEmpty()){
        int size = queue.size();
        for(int i = 0; i < size; i++){
            int current = queue.poll();
            for(int num : map.get(current)){
                if(!visited.contains(num)){
                    visited.add(num);
                    queue.add(num);
                }
            }
        }
        result += 1;
    }
    return result - 1;  // -1 because time taken by the first node to infect neighbours is 0
}

 public void inorder(TreeNode root, int prev, HashMap<Integer, HashSet<Integer>> map){
     if(root == null) return;
     if(!map.containsKey(root.val)) map.put(root.val, new HashSet<>());
     if(prev != -1){
         map.get(root.val).add(prev);
     }
     if(root.left != null) map.get(root.val).add(root.left.val);
     if(root.right != null) map.get(root.val).add(root.right.val);
     inorder(root.left, root.val, map);
     inorder(root.right, root.val, map);
 }

