class Solution {
    public int amountOfTime(TreeNode root, int start) {
        HashMap<Integer, List<Integer>> graph = new HashMap<>();
        buildGraph(root, graph);
        int ans = getTimeToInfact(graph, start);
        return ans;
    }
    static int getTimeToInfact(HashMap<Integer, List<Integer>> graph, int start) {
        int ans = -1;
        Set<Integer> infected = new HashSet<>();
        Queue<Integer> pending = new LinkedList<>();
        pending.add(start);
        infected.add(start);
        while(pending.size() != 0) {
            int len = pending.size();
            for(int i=0; i<len; i++) {
                int curr = pending.poll();
                List<Integer> child = graph.getOrDefault(curr, new ArrayList<>());
                for(int j=0; j<child.size(); j++) {
                    if(!infected.contains(child.get(j))) {
                        infected.add(child.get(j));
                        pending.add(child.get(j));
                    }
                }
            }
            ans++;
        }
        return ans;
    }
    static void buildGraph(TreeNode root, HashMap<Integer, List<Integer>> graph) {
        if (root == null) {
            return;
        }
        int curr = root.val;
        if(root.left != null) {
            int val = root.left.val;
            putNodeInGraph(curr, val, graph);
        }
        if(root.right != null) {
            int val = root.right.val;
            putNodeInGraph(curr, val, graph);
        }
        buildGraph(root.left, graph);
        buildGraph(root.right, graph);
    }
    static void putNodeInGraph(int curr, int val, HashMap<Integer, List<Integer>> graph) {
        if(!graph.containsKey(curr)) {
            graph.put(curr, new ArrayList<>());
        }
        if(!graph.containsKey(val)) {
            graph.put(val, new ArrayList<>());
        }
        List<Integer> currT = graph.get(curr);
        currT.add(val);
        graph.put(curr, currT);
        List<Integer> valT = graph.get(val);
        valT.add(curr);
        graph.put(val, valT);
    }
}
