use std::rc::Rc;
use std::cell::RefCell;
use std::collections::HashMap;
use std::collections::HashSet;
use std::collections::VecDeque;
type OptionNode = Option<Rc<RefCell<TreeNode>>>;

impl Solution {
    pub fn amount_of_time(root: OptionNode, start: i32) -> i32 {
        let mut graph = HashMap::<i32, Vec<i32>>::new();
        Self::collect(&mut graph, &root);

        let mut q = VecDeque::<i32>::new();
        let mut s = HashSet::<i32>::new();
        let mut count = 0;

        q.push_back(start);
        s.insert(start);
        while q.is_empty() == false {
            let sz = q.len();
            for _ in 0..sz {
                if let Some(u) = q.pop_front() {
                    if let Some(mp) = graph.get(&u) {
                        for v in mp {
                            if s.contains(&v) { continue }

                            s.insert(*v);
                            q.push_back(*v);
                        }
                    }
                } 
            }
            if q.is_empty() == false { count += 1; }
        }

        count
    }

    fn collect(graph: &mut HashMap<i32, Vec<i32>>, root: &OptionNode) {
        let node = root.as_ref().unwrap().borrow();

        if node.left.is_some() {
            let left = &node.left.as_ref().unwrap().borrow();

            graph.entry(node.val).or_insert(vec![]).push(left.val);
            graph.entry(left.val).or_insert(vec![]).push(node.val);

            Self::collect(graph, &node.left);
        }

        if node.right.is_some() { 
            let right = &node.right.as_ref().unwrap().borrow();

            graph.entry(node.val).or_insert(vec![]).push(right.val);
            graph.entry(right.val).or_insert(vec![]).push(node.val);

            Self::collect(graph, &node.right);
        }
    }
}
