/**
 * Definition for a binary tree node.
 * public class TreeNode {
 *     int val;
 *     TreeNode left;
 *     TreeNode right;
 *     TreeNode() {}
 *     TreeNode(int val) { this.val = val; }
 *     TreeNode(int val, TreeNode left, TreeNode right) {
 *         this.val = val;
 *         this.left = left;
 *         this.right = right;
 *     }
 * }
 */
class Solution {
    public int amountOfTime(TreeNode root, int start) {
        // build graph:
        Map<Integer, List<Integer>> graph = new HashMap<>();
        traverse(root, graph);

        // BFS:

        Queue<Integer> q = new LinkedList<>();
        HashSet<Integer> visited = new HashSet<>();

        q.add(start);
        visited.add(start);
        int level = -1;
        while (!q.isEmpty()) {
            int size = q.size();
            for (int s = 0; s < size; s++) {
                int curr = q.poll();

                List<Integer> neilist = graph.get(curr);
                for (int nei: neilist) {
                    if (!visited.contains(nei)) {
                        q.add(nei);
                        visited.add(nei);
                    }
                }
            }
            level++;
        }
        return level;
    }
    private void traverse(TreeNode root, Map<Integer, List<Integer>> graph) {
        if (root == null) return;
        // top down
        // root - left - right
        if (!graph.containsKey(root.val)) {
            graph.put(root.val, new ArrayList<>());
        }
        if (root.left != null) {
            if (!graph.containsKey(root.left.val)) {
                graph.put(root.left.val, new ArrayList<>());
            }
            graph.get(root.left.val).add(root.val);
            graph.get(root.val).add(root.left.val);
        }
        if (root.right != null) {

            if (!graph.containsKey(root.right.val)) {
                graph.put(root.right.val, new ArrayList<>());
            }
            graph.get(root.right.val).add(root.val);
            graph.get(root.val).add(root.right.val);
        }

        traverse(root.left, graph);
        traverse(root.right, graph);

    }
}
