```
/*
--> use a parent array to keep track of parent of a node
--> search for the start node 
--> with the help of given tree and parent node do a BSF
--> use a vis map to keep track of visited
--> increment ans after every level you traverse
*/
class Solution {
public:
    void pre(map<TreeNode*,TreeNode*> &parent,TreeNode* root,TreeNode* &st,int start)
    {
        if(!root) return;
        if(root->left) parent[root->left]=root;
        if(root->right) parent[root->right]=root;
        if(root->val==start) st=root;
        pre(parent,root->left,st,start);
        pre(parent,root->right,st,start);

    }
    int amountOfTime(TreeNode* root, int start) {
        map<TreeNode*,TreeNode*> parent;
        parent[root]=NULL;
        TreeNode* st;
        pre(parent,root,st,start); // traversal for marking parent of node and to search starting node // preorder traversal
        queue<TreeNode*> q;         q.push(st);
        int ans=0;
        map<TreeNode*,int> vis; // visited map
        vis[st]=1;
        while(!q.empty()){
            int n=q.size();
            for(int i=0;i<n;i++){
                TreeNode* curr=q.front();
                q.pop();
                if(curr->left && !vis[curr->left]){ q.push(curr->left); vis[curr->left]++;}
                if(curr->right && !vis[curr->right]){ q.push(curr->right); vis[curr->right]++;}
                if(parent[curr] && !vis[parent[curr]]){ q.push(parent[curr]); vis[parent[curr]]++;}
            }
            ans++;
        }
        return --ans;
    }
};
/*
--> TC O(N)
--> SC O(N)
*/
```

