solutions.content:
build graph: DFS
calculate the number of minute: BFS
```
class Solution {
    func amountOfTime(_ root: TreeNode?, _ start: Int) -> Int {
        var map = [Int: [Int]]()

        func dfs(_ node: TreeNode?, _ parentValue: Int?) {
            guard let node = node else { return }
            let val = node.val
            if let parentVal = parentValue {
                map[parentVal, default: [Int]()].append(val)
                map[val, default: [Int]()].append(parentVal)
            }
            dfs(node.left, val)
            dfs(node.right, val)
        }

        dfs(root, nil)

        var count = 0
        var queue = [start]
        var visited = Set<Int>()
        visited.insert(start)
        while queue.count > 0 {
            let size = queue.count
            for _ in 0..<size {
                let cur = queue.removeFirst()
                if let nexts = map[cur] {
                    for next in nexts {
                        if !visited.contains(next) {
                            queue.append(next)
                            visited.insert(next)
                        }
                    }
                }
            }
            count += 1
        }
        return count - 1
    }
}
