    public int dfs(TreeNode node) {
        if (node == null) return 0;
        int l = dfs(node.left);
        int r = dfs(node.right);
        map.put(node, new int[]{l, r});
        return Math.max(l, r) + 1;
    }

    public void dfs1(TreeNode node, int st, int up) {
        if (node == null) return;
        int[] lr = map.get(node);
        if (node.val == st) {
            ans = Math.max(up, Math.max(lr[0], lr[1]));
            return;
        }

        dfs1(node.left, st, Math.max(up, lr[1]) + 1);
        dfs1(node.right, st, Math.max(up, lr[0]) + 1);
    }
}
