```
/**
 * Definition for a binary tree node.
 * struct TreeNode {
 *     int val;
 *     TreeNode *left;
 *     TreeNode *right;
 *     TreeNode() : val(0), left(nullptr), right(nullptr) {}
 *     TreeNode(int x) : val(x), left(nullptr), right(nullptr) {}
 *     TreeNode(int x, TreeNode *left, TreeNode *right) : val(x), left(left), right(right) {}
 * };
 */
class Solution {
public:


    TreeNode* mark_parent(TreeNode* root, int val,unordered_map<TreeNode*,TreeNode*>&parent){
        TreeNode* start=nullptr;
        queue<TreeNode*>q;
        q.push(root);
        while(!q.empty()){ 
            TreeNode* node=q.front();
            q.pop();
            //if we found the nide then simply return
            if(node->val==val){
                start=node;
            }

            //mark parent of left child
            if(node->left){
                parent[node->left]=node;
                q.push(node->left);

            }
            //mark parent of right child
             if(node->right){
                parent[node->right]=node;
                q.push(node->right);
            }
        }
        return start;
    }
    int amountOfTime(TreeNode* root, int start) {

        //mark the parent of each node
        unordered_map<TreeNode*,TreeNode*>parent;

        //this will mark the parent of each node and return the address of starting node
        TreeNode* startNode=mark_parent(root,start,parent);

        //this will keep track of the node whitch are already infected
        unordered_map<TreeNode*,bool>visited;


        queue<TreeNode*>q;
        q.push(startNode);
        visited[startNode]=true;

        int time=0;

        //level ordered
        while(!q.empty()){
            int currSize=q.size();
            bool infect=false;

            //at one time all the node which are infected at the current level will infect all the adjacent nodes to them

            while(currSize--){
                TreeNode* node=q.front();
                q.pop();

                //if left child exist and not infected then mark
                if(node->left and !visited[node->left]){
                    q.push(node->left);
                    visited[node->left]=true;
                    infect=true;
                }
                  //if right child exist and not infected then mark
                 if(node->right and !visited[node->right]){
                    q.push(node->right);
                    visited[node->right]=true;
                    infect=true;
                }
                  //if parent of current nodeexist and not infected then mark and push to queue for next process 
                 if(parent[node] and !visited[parent[node]]){
                    q.push(parent[node]);
                    visited[parent[node]]=true;
                    infect=true;
                }
            }

            //time will only increase if any of the node at current level infected any of it's adjacent node;
            if(infect) time++;
        } 
    return time;   
    }
};
```

