class Solution {
public:
    TreeNode* s = NULL;
    int start = 0;
    void solve(unordered_map<TreeNode*, TreeNode*> &par, TreeNode* root){
        if(root==NULL) return;
        if(root->left){
            par[root->left] = root;
        }
        if(root->right){
            par[root->right] = root;
        }
        if(root->val==start){
            s = root;
        }
        solve(par, root->left);
        solve(par, root->right);
    }
    int amountOfTime(TreeNode* root, int st) {
        if(root==NULL) return 0;
        unordered_map<TreeNode*, TreeNode*> par;
        unordered_set<TreeNode*> vis;
        start = st;
        solve(par, root);
        queue<TreeNode*> q;
        q.push(s);
        vis.insert(s);
        int time = 0;
        while(!q.empty()){
            int n = q.size();
            for(int i = 0; i<n; i++){
                auto t = q.front();
                q.pop();
                if(t->left and vis.find(t->left)==vis.end()){
                    vis.insert(t->left);
                    q.push(t->left);
                }
                if(t->right and vis.find(t->right)==vis.end()){
                    vis.insert(t->right);
                    q.push(t->right);
                }
                if(par[t] and vis.find(par[t])==vis.end()){
                    vis.insert(par[t]);
                    q.push(par[t]);
                }
            }
            time++;
        }
        return time-1;
    }
};
