class Solution {
    TreeNode target;
    public int amountOfTime(TreeNode root, int start) {
        Map<TreeNode,TreeNode> parents=new HashMap<>();
        getParents(root,null,parents);
        findNode(root,start);
        Set<TreeNode> visited= new HashSet<>();
        int x=helper(target,parents,visited);
        return --x;
    }
    private int helper(TreeNode curr,Map<TreeNode,TreeNode> map,Set<TreeNode> visited){
        if(curr==null && visited.contains(curr))
            return 0;
        visited.add(curr);
        int left= curr.left==null || visited.contains(curr.left)? 0 :helper(curr.left,map,visited);
        int right= curr.right==null || visited.contains(curr.right)? 0:helper(curr.right,map,visited);
        int parent= map.get(curr)==null || visited.contains(map.get(curr)) ?0:helper(map.get(curr),map,visited);

        return 1+   Math.max(left,Math.max(right,parent));

    }
    private void findNode(TreeNode curr,int start){
        if(curr==null)
            return;
        if(curr.val==start)
            target=curr;
        findNode(curr.left,start);
        findNode(curr.right,start);
    }
    private void getParents(TreeNode curr,TreeNode parent,Map<TreeNode,TreeNode> map){
        if(curr==null)
            return;
        if(!map.containsKey(curr))
            map.put(curr,parent);
        getParents(curr.left,curr,map);
        getParents(curr.right,curr,map);

    }
}
