	//  step-1 : first we are filling the parent of each node in a parent map ( because it will be needed while going up into the tree)
	//  step-2 : then we are finding the start node in  the tree and pushing that into queue 
	//  step-3 : also creating visited map for rembering visited nodes
	//	step-4 : then doing a simple bfs to find out time 
	//	step-5 : after each step of bfs we are increasing the time counter because at each time virus will spread to end nodes by one step only

	void fillparent(TreeNode* node,map<TreeNode*,TreeNode*> &parent,TreeNode* par)         //for finding and putting parent of each node by passing current node to their child nodes
    {
        if(!node     return;

        parent[node] = par;                       //assinging parent to the node
        fillparent(node->left,parent,node);
        fillparent(node->right,parent,node);        
    }

    bool fillq(queue<TreeNode*> &q,int start,TreeNode* root)         //for finding the start node and pushing it into the queue
    {
        if(!root)      return false;

        if(root->val == start)
        {
            q.push(root);
            return true;
        }
        if(fillq(q,start,root->left) || fillq(q,start,root->right))            // if start node is pushed then break the dfs call and return true else return false 
            return true;

        return false;
    }

    int bfs(map<TreeNode*,TreeNode*> parent,TreeNode* root,int start)
    {
        map<TreeNode*,int> vis;
        queue<TreeNode*> q;
        fillq(q,start,root);                                       //calling fillq for pushing start into the queue
        int t=0;
        auto n = q.front();
        vis[n]=1;                                                    //marking start to visited

        while(!q.empty())
        {
            int size = q.size();
            for(int i=0;i<size;i++)
            {
                auto nd = q.front();
                q.pop();
                if(nd->left != NULL && vis.find(nd->left)==vis.end())         //checking for left child node and if not visited pushing into the queue
                {
                    vis[nd->left]=1;
                    q.push(nd->left);
                }
                if(nd->right != NULL && vis.find(nd->right)==vis.end())         //checking for right child node and if not visited pushing into the queue
                {
                    vis[nd->right]=1;
                    q.push(nd->right);
                }
                if(parent[nd] != NULL && vis.find(parent[nd])==vis.end())         //checking for parent node and if not visited pushing into the queue
                {
                    vis[parent[nd]]=1;
                    q.push(parent[nd]);
                }                
            }
            t++;                            // increasing time after each step of virus spreading
        }

        return t-1;                          // for target node time will always be 0 so returning t-1
    }

    int amountOfTime(TreeNode* root, int start) 
    {
        map<TreeNode*,TreeNode*> parent;        
		fillparent(root,parent,NULL);
        return bfs(parent,root,start);
