from collections import deque, defaultdict
class Solution:
    def amountOfTime(self, root: Optional[TreeNode], start: int) -> int:
        # go through entire tree with depth first search to 
        # easily keep track of relationship between nodes
        def dfs(prev, curr):
            if prev: graph[curr.val].append(prev.val)
            if curr.left:
                graph[curr.val].append(curr.left.val)
                dfs(curr, curr.left)
            if curr.right:
                graph[curr.val].append(curr.right.val)
                dfs(curr, curr.right)

        # build undirected graph from the tree with depth first search
        graph = defaultdict(list)
        dfs(None, root)

        # go through entire graph with breath first search to count
        # how long it'll take to infect all the nodes
        ans = -1
        queue, visited = deque([start]), set([start])
        while queue:
            ans += 1
            lvsize = len(queue)
            for _ in range(lvsize):
                node = queue.popleft()
                for neigh in graph[node]:
                    if neigh not in visited:
                        queue.append(neigh)
                        visited.add(neigh)
        return ans

