/**
 * Definition for a binary tree node.
 * public class TreeNode {
 *     int val;
 *     TreeNode left;
 *     TreeNode right;
 *     TreeNode() {}
 *     TreeNode(int val) { this.val = val; }
 *     TreeNode(int val, TreeNode left, TreeNode right) {
 *         this.val = val;
 *         this.left = left;
 *         this.right = right;
 *     }
 * }
 */
class Solution {
    public void getParent(TreeNode root,HashMap<TreeNode,TreeNode> parent){
        if(root==null) return;
        if(root.left!=null){
            parent.put(root.left,root);
        }
        if(root.right!=null){
            parent.put(root.right,root);
        }

        getParent(root.left,parent);
        getParent(root.right,parent);
    }
    public TreeNode find(TreeNode root, int target){
        if(root==null) return null;
        if(root.val==target) return root;

        TreeNode filc=find(root.left,target);
        if(filc!=null) return filc;

        TreeNode firc=find(root.right,target);
        if(firc!=null) return firc;

        return null;
    }
    public int amountOfTime(TreeNode root, int start) {
        HashMap<TreeNode,TreeNode> parent=new HashMap<>();
        getParent(root,parent);

        TreeNode src=find(root,start);


        Queue<TreeNode> que=new LinkedList<>();
        HashSet<TreeNode> vis=new HashSet<>();

        que.add(src);
        vis.add(src);

        int level=0;

        while(que.size()>0){
            int size=que.size();
            while(size-->0){
                TreeNode rnode=que.remove();
                if(rnode.left!=null && vis.contains(rnode.left)==false){
                    que.add(rnode.left);
                    vis.add(rnode.left);
                }

                if(rnode.right!=null && vis.contains(rnode.right)==false){
                    que.add(rnode.right);
                    vis.add(rnode.right);
                }

                if(parent.get(rnode)!=null && vis.contains(parent.get(rnode))==false){
                    que.add(parent.get(rnode));
                    vis.add(parent.get(rnode));
                }
            }
            level++;
        }
        if(level<0) return 0;
        return level-1;
    }
}
