var amountOfTime = function(root, start) {
    if(!root){
        return 0; // Return a default value when root is falsy
    }

    let hashmap = new Map();

    function bfs(node){
        let queue = [];
        queue.push(node);
        while(queue.length > 0){
            let size = queue.length;
            for(let i = 0; i < size; i++){
                let curr = queue.shift();
                if (!hashmap.has(curr.val)){
                    hashmap.set(curr.val,[]);
                }
                if(curr.left){
                    if (hashmap.has(curr.val)){
                        hashmap.get(curr.val).push(curr.left.val);
                        hashmap.set(curr.left.val,[curr.val]);//as undorected 
                    }
                    queue.push(curr.left);
                }
                if(curr.right){
                    if (hashmap.has(curr.val)){
                        hashmap.get(curr.val).push(curr.right.val);
                        hashmap.set(curr.right.val,[curr.val]);//as undorected 
                    }
                    queue.push(curr.right);
                }
            }
        }
    }

    bfs(root);
    let queue = [];
    let visited = new Set();
    queue.push({node : start, time : 0});
    let maxTime = 0;

    while(queue.length > 0){
        let size = queue.length;
        for(let i = 0; i < size; i++){
            let {node, time} = queue.shift();
            maxTime = Math.max(time, maxTime);
            visited.add(node);
            console.log(hashmap.get(node), "cjecl");
            for (let nei of hashmap.get(node)) {
                if(!visited.has(nei)){
                    queue.push({node : nei, time : time + 1});
                }
            }
        }
    }

    return maxTime;
};

