/**
 * Definition for a binary tree node.
 * public class TreeNode {
 *     int val;
 *     TreeNode left;
 *     TreeNode right;
 *     TreeNode() {}
 *     TreeNode(int val) { this.val = val; }
 *     TreeNode(int val, TreeNode left, TreeNode right) {
 *         this.val = val;
 *         this.left = left;
 *         this.right = right;
 *     }
 * }
 */
class Solution {
    public int amountOfTime(TreeNode root, int start) {
        Map<Integer, List<Integer>> graph = new HashMap<>();
        buildGraph(null, root, graph);
        Set<Integer> infected = new HashSet<>();
        infected.add(start);
        Queue<Integer> queue = new LinkedList<>();
        queue.add(start);
        int minute = 0;
        while(!queue.isEmpty())
        {
            int count = queue.size();
            for(int index = 0;index < count;index++)
            {
                Integer curr = queue.poll();
                for(var neighbor:graph.get(curr))
                {
                    if(!infected.contains(neighbor))
                    {
                        infected.add(neighbor);
                        queue.add(neighbor);
                    }
                }
            }
            minute++;
        }
        return minute-1;
    }

    private void buildGraph(TreeNode parent, TreeNode curr, Map<Integer, List<Integer>> graph)
    {
        if(curr == null) return;
        graph.put(curr.val, new ArrayList<>());
        TreeNode[] neighbors = new TreeNode[] {parent,curr.left,curr.right};
        for(TreeNode neighbor:neighbors)
        {
            if(neighbor!=null) graph.get(curr.val).add(neighbor.val);
        }
        buildGraph(curr,curr.left,graph);
        buildGraph(curr,curr.right,graph);
    }
}
