 *         this.left = left;
 *         this.right = right;
 *     }
 * }
 */
class Solution {

    public int amountOfTime(TreeNode root, int start) {
        HashMap<TreeNode, TreeNode> parent = new HashMap<>();
        TreeNode target = makeParent(root , parent , start);
        int ans = findTime(root , parent , target);
        return ans;
    }

    public TreeNode makeParent(TreeNode root,HashMap<TreeNode, TreeNode> parent, int start){
        Queue<TreeNode> q = new LinkedList<>();
        q.add(root);
        TreeNode res = null;
        while(!q.isEmpty()){
            TreeNode curr = q.poll();
            if(curr.val == start) res = curr;

            if(curr.left != null){
                parent.put(curr.left , curr);
                q.add(curr.left);
            }
             if(curr.right != null){
                parent.put(curr.right , curr);
                q.add(curr.right);
            }
        }
        return res;
    }

    public int findTime(TreeNode root,HashMap<TreeNode, TreeNode> parent, TreeNode target){
        if(root == null) return 0;
        HashMap<TreeNode,Integer> visited = new HashMap<>();
        Queue<TreeNode> q = new LinkedList<>();
        visited.put(target , 1);
        q.add(target);
        int time = 0;
        while(!q.isEmpty()){
            int n = q.size();
            int flag = 0;
            for(int i = 0 ; i<n ; i++){
                TreeNode curr = q.poll();
                if(curr.left != null && visited.get(curr.left) == null){
                    flag = 1;
                    visited.put(curr.left, 1);
                    q.add(curr.left);
                }
                 if(curr.right != null && visited.get(curr.right) == null){
                    flag = 1;
                    visited.put(curr.right, 1);
                    q.add(curr.right);
                }
                 if(parent.get(curr) != null && visited.get(parent.get(curr)) == null){
                    flag = 1;
                    visited.put(parent.get(curr), 1);
                    q.add(parent.get(curr));
                }
            }
            if(flag == 1) time++;
        }
        return time;
    }
}
