/**
 * Definition for a binary tree node.
 * public class TreeNode {
 *     int val;
 *     TreeNode left;
 *     TreeNode right;
 *     TreeNode() {}
 *     TreeNode(int val) { this.val = val; }
 *     TreeNode(int val, TreeNode left, TreeNode right) {
 *         this.val = val;
 *         this.left = left;
 *         this.right = right;
 *     }
 * }
 */
class Solution {
    public int amountOfTime(TreeNode root, int start) {
        HashMap<TreeNode, TreeNode> parentMap = new HashMap<>();
        HashMap<TreeNode, Boolean> visitedMap = new HashMap<>();
        TreeNode target = makeParents(root, parentMap, start);
        int ans = heightTreeTarget(target, parentMap, visitedMap);
        return ans - 1;
    }
    public TreeNode makeParents(TreeNode root, HashMap<TreeNode, TreeNode> map, int start)
    {
        Queue<TreeNode> q = new LinkedList<>();
        q.add(root);
        TreeNode res = new TreeNode(-1);
        while(!q.isEmpty())
        {
            TreeNode node = q.poll();
            if(node.val == start)
                res = node;
            if(node.left!=null)
            {
                map.put(node.left, node);
                q.add(node.left);
            }
            if(node.right!=null)
            {
                map.put(node.right, node);
                q.add(node.right);
            }
        }
        return res;
    }
    public int heightTreeTarget(TreeNode root, HashMap<TreeNode, TreeNode> parentMap, HashMap<TreeNode, Boolean> visitedMap)
    {
        if(root == null)
            return 0;

        visitedMap.put(root, true);

        int left = 0, right = 0, parent = 0;
        if(visitedMap.get(root.left)==null)
            left = heightTreeTarget(root.left, parentMap, visitedMap);

        if(visitedMap.get(root.right)==null)
            right = heightTreeTarget(root.right, parentMap, visitedMap);

        if(visitedMap.get(parentMap.get(root))==null)
            parent = heightTreeTarget(parentMap.get(root), parentMap, visitedMap);  

        return Math.max(left, Math.max(right, parent)) + 1;

    }
}
