class Solution {
private:
    void findparent(TreeNode *root,unordered_map<TreeNode*,TreeNode*>&mp){
        queue<TreeNode*>q;
        q.push(root);
        while(!q.empty()){
            auto node = q.front();
            q.pop();
            if(node->left){
                q.push(node->left);
                mp[node->left]=node;
            }
            if(node->right){
                q.push(node->right);
                mp[node->right]=node;
            }
        }
    }
    TreeNode* getnode(TreeNode *root,int target){
        queue<TreeNode*>q;
        q.push(root);
        while(!q.empty()){
            auto node = q.front();
            q.pop();
            if(node->val==target) return node;
            if(node->left) q.push(node->left);
            if(node->right) q.push(node->right);
        }
        return nullptr;
    }
public:
    int amountOfTime(TreeNode* root, int start) {
        //mark all of the nodes with their parents 
        unordered_map<TreeNode*,TreeNode*>mp;
        findparent(root,mp);
        //now we find the affected node 
        auto targetnode=getnode(root,start);
        //now we will make a visited map to make sure that when we are going from
        //the target node we do not really end reaching the prev node itself 
        unordered_map<TreeNode*,bool>mpp;
        //now let us start the bfs from the target node 
        queue<TreeNode*>q;
        q.push(targetnode);
        mpp[targetnode]=true;
        int count=0;
        while(!q.empty()){
            count++;
            int sz=q.size();
            for(int i=0;i<sz;i++){
                auto node=q.front();
                q.pop();
                if(node->left and !mpp[node->left]){
                    q.push(node->left);
                    mpp[node->left]=true;
                }
                if(node->right and !mpp[node->right]){
                    q.push(node->right);
                    mpp[node->right]=true;
                }
                //after affecting nodes in the children side 
                //we now look for affecting nodes if we go via the parent side 
                if(mp[node] and !mpp[mp[node]]){
                    q.push(mp[node]);
                    mpp[mp[node]]=true;
                }
            }
        }
        return count-1;
    }
};
