 * struct TreeNode {
 *     int val;
 *     TreeNode *left;
 *     TreeNode *right;
 *     TreeNode() : val(0), left(nullptr), right(nullptr) {}
 *     TreeNode(int x) : val(x), left(nullptr), right(nullptr) {}
 *     TreeNode(int x, TreeNode *left, TreeNode *right) : val(x), left(left), right(right) {}
 * };
 */
class Solution {
public:
    void dfs(TreeNode* root, unordered_map<TreeNode*, TreeNode*>&mp, TreeNode* parent){
        if(!root) return;
        mp[root] = parent;
        dfs(root->left, mp, root);
        dfs(root->right, mp, root);
    }

   TreeNode* findTarget(TreeNode* root, int start){
        if(!root) return NULL;
        if(root->val == start){
            return root;
        }
        TreeNode* l = findTarget(root->left, start);
        if(l) return l;
        TreeNode* r = findTarget(root->right, start);
        return r;

    }
    int amountOfTime(TreeNode* root, int start) {
        unordered_map<TreeNode*, TreeNode*>mp;
        dfs(root,mp,NULL);
        TreeNode* target = findTarget(root,start);
        queue<TreeNode*>q;
        unordered_map<TreeNode*, bool>vis;
        q.push(target);
        vis[target]=true;
        int count =-1;
        while(!q.empty()){
            int size = q.size();
            for(int i=0; i<size; i++){
                auto temp = q.front();
                q.pop();
                if(!vis[mp[temp]] and mp[temp]){
                    q.push(mp[temp]);
                    vis[mp[temp]]=true;
                }
                if(!vis[temp->left] and temp->left){
                    q.push(temp->left);
                    vis[temp->left]= true;
                }
                if(temp->right and !vis[temp->right]){
                    q.push(temp->right);
                    vis[temp->right] = true;
                }
            }
            count++;
        }

        return count;

    }
};
