            prevNode.next = node2;                              // ...[prev]-->[node2]<--[node1]   [next]...
            node2.next = node1;                                 // ...[prev]-->[node2]<-->[node1]  [next]...
            node1.next = nextNode;                              // ...[prev]-->[node2]-->[node1]-->[next]...

            //Move 
            prevNode = node1;                                   // ...[...]-->[node2]-->[node1/prev]-->[next]...
        }

        return newHead.next;
    }
