```swift
class Solution {
    func swapPairs(_ head: ListNode?) -> ListNode? {
        var head = head, node = head, pre: ListNode?

        while node != nil, let next = node!.next {
            let cur = next, tmp = cur.next
            pre == nil ? (head = cur) : (pre!.next = cur)
            cur.next = node
            node!.next = tmp
            pre = node
            node = tmp
        }
        return head
    }
}
```

