class Solution {
public:
    int partitionString(string s) {
        int cnt = 0;
        int streak = 0;
        vector<int> vis(26 , 0);
        vis[s[0] - 'a'] = 1;
        for(int i = 1 ; i < s.size(); i++){
            if(vis[s[i] - 'a'] == 0){
                vis[s[i] - 'a'] = 1;
            }
            else{
                cnt++;
                for(int i = 0 ; i < 26 ; i++){
                    vis[i] = 0;
                }
                vis[s[i] - 'a'] = 1;
            }
        }
        for(int i = 0 ; i < 26 ; i++){
            if(vis[i] == 1){
                cnt++;
                break;
            }
        }
        return cnt;
    }
};
```

