class Solution {
    public int partitionString(String s) {
        Set<Character> set = new HashSet<>();

        int totalCount = 1; // nonempty string can be always in fit 1 partion

        for(char ch : s.toCharArray()) {
             // If current char is not present in set, add it
            if(!set.contains(ch)) {
                set.add(ch);
            } else {
               // if current char is present, clear the set and add current element, increase totalcount.
                set.clear();
                set.add(ch);
                totalCount++;
            }
        }
        return totalCount;
    }
}
