class Solution {
public:
    int partitionString(string str) {

        int n = str.size();

        int count = 0;

        // set will store the no. of different characters of a substring

        unordered_set<char> s;

        int i = 0;

        while(i < n)
        {
            // store the characters in set untill we encounter a duplicate character

            while(i < n && s.count(str[i]) == 0)
            {
                s.insert(str[i]);

                i++;
            }

            // clear set for next substring

            s.clear();

            // increment count of substrings

            count++;
        }

        return count;
    }
};
