impl Solution {
    pub fn partition_string(s: String) -> i32 {
        let mut res = 1;
        let mut mask = 0;
        for c in s.chars() {
            let cur = 1 << (c as u8 - b'a');
            if (mask & cur) == cur {
                res += 1;
                mask = 0;
            }
            mask ^= cur;
        }
        res
    }
}

