class Solution {
    public int partitionString(String s) {   
        return partitionStringRecursion(s, 0, new HashSet<>(), new Integer[s.length()+1]);
    }

    private int partitionStringRecursion(String s, int index, Set<Character> chars, Integer[] cache) {
        // Base Condition
        if (index == s.length()) {
            return 1;
        }

        if (cache[index] != null) {
            return cache[index];
        }

        int charCanBeAdded = 0, charCannotBeAdded = 0;
        if (chars.add(s.charAt(index))) {
            charCanBeAdded = partitionStringRecursion(s, index+1, chars, cache);
        } else {
            charCannotBeAdded = 1 + partitionStringRecursion(s, index, new HashSet<>(), cache);
        }
        return cache[index] = Math.max(charCanBeAdded, charCannotBeAdded);
    }
}
