function partitionString(s: string): number {
    const set = new Set()
    let ans = 1

    for (let right = 0; right < s.length; right++) {
        if (set.has(s[right])) {
            set.clear()
            ans += 1
        }

        set.add(s[right])
    }

    return ans
};
