    int partitionString(string s) {
        int n = s.size();
        int ans = 1;

        int mask = 0;
        for (auto i: s) {
            int x = i-'a';
            if ((mask >> x) & 1) {
                ans++;
                mask = 0;
            }

            mask = mask | (1<<x);
        }

        return ans;
    }
};
