class Solution {
public:
    int partitionString(string s) {

        int two_pow[26]; // prepare the mask value for the letter 'a' to 'z'
        for (int d = 0; d < 26; d++){
            if (d == 0) two_pow[d] = 1;
            else two_pow[d] = two_pow[d - 1] * 2;
        }

        int sn = (int)s.size();
        int start = 0;
        int ret = 0;
        while(true){
            int mask = 0;
            int update_start = -1;
            for (int i = start; i < sn; i++){
                int letter = s[i] - 'a';
                if ((two_pow[letter] & mask) == 0){// use bitmask to check whether s[i] is duplicated.
                    mask |= two_pow[letter];
                }
                else{
                    update_start = i;
                    ret++;
                    break;
                }                
            }
            if (update_start == -1) {
                ret += (bool)mask;
                break;
            }
            start = update_start;
        }
        return ret;
    }
};
