// Solution 1 - 2 for loops - SC O(1)
class Solution {
public:
    int trap(vector<int>& h) {
        int ans = 0, currTop = 0, final = 0;
        for(int i = 0; i<h.size(); i++){
            if(h[i] < h[currTop]){
                ans += h[currTop] - h[i];
            }
            else{
                currTop = i;
                final += ans;
                ans = 0;
            }
        }
        ans = 0;
        int prevTop = currTop;
        currTop = h.size()-1;
        for(int i = currTop; i>=prevTop; i--){
            if(h[i] < h[currTop]){
                ans += h[currTop] - h[i];
            }
            else{
                currTop = i;
                final += ans;
                ans = 0;
            }
        }
        return final;
    }
};

// Solution 2 - Two pointer approach - SC O(1)
class Solution {
public:
    int trap(vector<int>& height) {
        int n = height.size(), lmax = height[0], rmax = height[n-1];
        int lpos = 1, rpos = n-2, ans = 0;
        while(lpos <= rpos){
            if(height[lpos] >= lmax){
                lmax = height[lpos];
                lpos++;
            }
            else if(height[rpos] >= rmax){
                rmax = height[rpos];
                rpos--;
            }
            else if(lmax <= rmax && height[lpos] < lmax){
                ans += lmax - height[lpos];
                lpos++;
            }
            else{
                ans += rmax - height[rpos];
                rpos--;
            }
        }
        return ans;
    }
};

// Solution 3 - Stack - SC O(N)
class Solution {
public:
    int trap(vector<int>& height) {
        if(height.empty()) 
            return 0; 

        int res = 0;
        stack<int> s;   
        for(int i=0; i<height.size(); i++) {
            while(!s.empty() && height[i] > height[s.top()]) {
                int rightIdx = s.top();
                s.pop();

                if(s.empty()) 
                    break;

                int leftIdx = s.top();
                int length = min(height[i], height[leftIdx]) - height[rightIdx];
                int breadth = i - leftIdx - 1;
                res += length*breadth;
            }
            s.push(i);
        }
        return res;
    }
};
```

