//Approach-1 (Without stack) Simple approach. Time : O(n)
class Solution {
public:
    int n;
    vector<int> left, right;
    void leftMax(vector<int>& height) {
        left[0] = height[0];
        for(int i = 1; i<n; i++) {
            left[i] = max(left[i-1], height[i]);
        }
    }

    void rightMax(vector<int>& height) {
        right[n-1] = height[n-1];
        for(int i = n-2; i>=0; i--) {
            right[i] = max(right[i+1], height[i]);
        }
    }

    int trap(vector<int>& height) {
        n = height.size();
        if(n == 0 || n == 1) 
            return 0;

        left.resize(n);
        right.resize(n);

        leftMax(height);
        rightMax(height);

        int ans = 0;
        for(int i = 0; i<n; i++) {
            ans += ((min(left[i], right[i])-height[i]));
        }
        return ans;   
    }
};
```


```
//Approach-2 (Using stack, a smart approach) Time : O(n) all elements are pushes and pop once
class Solution {
public:
    int trap(vector<int>& height) {
        int n = height.size();
        stack<int> st;

        int water = 0;

        for(int i = 0; i<n; i++) {
            while(!st.empty() && height[i] > height[st.top()]) {
                //It means index is bounded from left (i.e. height[st.top()]) and right(i.e. height[i])
                int index = st.top();
                st.pop();

                //This index can't contribute any more 
                //(Example : [4,2,0,3,2,5]), we will be left with empty stack at end
                //while processing 5
                if(st.empty())
                    break;

                int h = min(height[i], height[st.top()]) - height[index];
                int w = i - st.top() - 1; //same concept as simplfied MAH

                water += h*w;
            }
            st.push(i);
        }
        return water;
    }
};
```

