class Solution {
    public int trap(int[] height) {
        int n= height.length;
        int trapped=0;
        // max left boundary
        int left[]= new int [n];
        left[0]= height[0];
        for(int i=1; i<n; i++){
            left[i]= Math.max(left[i-1], height[i]);   
        }

        // max right boundary
        int right[]= new int [n];
        right[n-1]= height[n-1];
        for(int i=n-2; i>=0; i--){
            right[i]= Math.max(height[i] ,right[i+1]);
        }

        // loop
        for(int i=0; i<n;i++){
            int water= Math.min(left[i], right[i]);
            trapped= trapped+(water-height[i]);
        }
        return trapped;
    }
