class Solution {
public:
    /*
     * Fast Slow Pointers Solution
     * Assume the cycle start node is node_x, it's position is x, it's cycle length is y
     * Fast pointer move 2 step at once, slow pointer move 1 step at once,
     * When fast pointer and slow pointer first meet(except head), assume they meet the node ahead of node_x t steps 
     * The total steps of Slow Pointer is
     * T_slow = x + t                        (1)
     * The total steps of Fast Pointer is
     * 2*T_slow = x + t +ky                  (2)
     * From (2) - (1), we get
     * T_slow = ky,
     * then from (1), we get
     * x = ky -t,
     * So fromt node(x+t), we can run k cycle to get x.
     */
    ListNode *detectCycle(ListNode *head) {
        auto fast_node = head;
        auto slow_node = head;
        bool is_cycle = false;
        while(fast_node){
            fast_node = fast_node->next;
            if(fast_node){
                fast_node = fast_node->next;
                slow_node = slow_node->next;
                if(fast_node == slow_node){
                    is_cycle = true;
                    break;
                }
            }
        }
        if(!is_cycle){
            return NULL;
        }
        auto detect_node = head;
        while(detect_node != slow_node){
            detect_node = detect_node->next;
            slow_node = slow_node->next;
        }
        return detect_node;
    }
};
```

