public class Solution 
{
    public ListNode detectCycle(ListNode head) // Implementation of Floyd Cycle detection algorithm.
    {  
        ListNode tortoise, hare; // Set two pointers, one is twice as faster then the other.

        tortoise = head; // Slow pointer.

        hare = head; // Fast pointer.

        while(hare != null && hare.next != null) // While the current and next elements on which the fast pointer is pointing are not null, continue.
        {
            tortoise = tortoise.next;
            hare = hare.next.next;

            if(hare == tortoise) // If the pointers point at the same element, thats mean that a cycle exist, NOTICE that dose not mean that the element is the start
            {                   // of the cycle!
                   break;       // Break the loop, think of it as stopping the pointers were they stand. 
            }         
        }

        if(hare == null || hare.next == null) // Check if the fast pointer is null or the next element is null as well.
        {
           return null;
        } 

        tortoise = head; // Set the slow pointer to the start of the list.

        while(tortoise != hare) // As long as the pointers do not point at each other, continue.
        {
            tortoise = tortoise.next;

            hare = hare.next; // Match the speed of the faster pointer to the speed of the slow pointer.  
        }
        return tortoise; // Floyd Cycle detection algorithm guarantee that when the pointers meet for the second time it will be at the starting point of the cycle!

    }
