/**
 * Definition for singly-linked list.
 * class ListNode {
 * int val;
 * ListNode next;
 * ListNode(int x) {
 * val = x;
 * next = null;
 * }
 * }
 */
public class Solution {
   public ListNode detectCycle(ListNode head) {

      //If head equal null
      // the do nothing

      if (head == null)
         return null;


      //create slow and fast pointer
      ListNode slow = head;
      ListNode fast = head;

      boolean cycleDetected = false;

      // If there will be a cycle then fast.next.next never be null
      // So, Do not confuse with the condition
      // if there's no cycle then, fast.next will be null

      while (fast.next != null && fast.next.next != null) {

         slow = slow.next;

         // if cycleDetected detected move one step forword for fast pointer as well
         if (cycleDetected)
            fast = fast.next;
         else
            fast = fast.next.next; // else move 2 steps forword.

         if (slow == fast && !cycleDetected) {

             // if slow == fast first time that time, toggle the cycleDetected 
             // i.e : cycleDetected  = true
            slow = head;
            cycleDetected = true;

         }
         if (slow == fast && cycleDetected)
             // Second time return 
            return slow;
      }

       // not found cycle
      return null;
   }
}
```

