/**
 * Definition for singly-linked list.
 * struct ListNode {
 *     int val;
 *     ListNode *next;
 *     ListNode(int x) : val(x), next(NULL) {}
 * };
 */
class Solution {
public:
    ListNode *detectCycle(ListNode *head) {
        //initialize a set to store the elements of linked list
        unordered_set<ListNode*> nodes; 

        //traverse the linked list
        ListNode* temp = head;
        while(temp != NULL){
            //if we encounter a node which is already present in the set -> return the node
            if(nodes.find(temp) != nodes.end()){
                return temp;
            }

            //if node is not present in the set -> insert to set
            nodes.insert(temp);
            temp = temp->next;
        }

        //if don't find any such node -> return null
        return NULL;
    }
};
```

