            fast=fast->next->next;                 //Fast is moving with speed of 2 nodes.
            if(slow==fast)
                 break;
        }
        if(slow!=fast)                   // If there is no loop then return NULL
            return NULL;                                                     
        slow=head;                   //We have moved the slow pointer to head.
        while(slow!=fast)          // Loop removal concept
        {
            slow=slow->next;
            fast=fast->next;
        }
        return fast;                // Fast and Slow will always meet at the entry of loop which we have to return
    }
