public ListNode useTwoPointers(ListNode head) {
  ListNode slow = head;
  ListNode fast = head;
  while (fast != null && fast.next != null) {
    slow = slow.next;
    fast = fast.next.next;
    if (fast == slow) { // meets
      while (head != slow) { // moves from the head until they meet
        head = head.next;
        slow = slow.next;
      }
      return head;
    }
  }
  return null;
}
