        var dict = new Dictionary<char, int>();
        foreach(var i in s)
            dict[i] = dict.ContainsKey(i) ? (dict[i] + 1) : 1;
        foreach(var i in t)
            if(dict.ContainsKey(i)){
                dict[i] -= 1;
                if(dict[i] < 0) return false;
            }
