        # iterate over t string and reduce count if the char found in hashmap
        for c in t:
            if c in hashmap:
                hashmap[c] -= 1
            else:
                return False

        # if the anagram is valid the count of each char in the hashmap is 0
        return all(value == 0 for value in hashmap.values())
