            ans[i] += (long long)(a[i].x + 0.5); 
            ans[i+1] += ans[i] / 100000; 
            ans[i] %= 100000; 
        } 
        while (ans[n]) {
            ans[n+1] = ans[n] / 100000; 
            ans[n] %= 100000; 
            ++n; 
        } 
        while (n > 1 && ans[n-1] == 0) --n; 


        stringstream s;
        for(int i = n - 1; i >= 0; --i){
            s << (int)ans[i];
            s << setw(5) << setfill('0');
        }

        delete[] a;
        delete[] b;
        delete[] ans;
        return s.str();
