            if (i_carry > 0)
                result[i + j + 1] = i_carry;
            i_carry = 0;
        }

        while (result[0] == 0 && result.size() > 1)
            result.erase(result.begin());

        for (i = 0; i < result.size(); i++)
        {
            str_result += '0' + result[i];
        }

        return str_result;
    }
