            for (int i = 0; i < n; i += len) {
                for (int j = 0; j < len / 2; j++) {
                    Complex u = a[i + j];
                    Complex v = omega[j * n / len] * a[i + j + len / 2];
                    a[i + j] = u + v;
                    a[i + j + len / 2] = u - v;
                }
            }
        }
    }

    string multiply(string num1, string num2) {
        if (num1 == "0" || num2 == "0") {
