class Solution:
    def multiply(self, num1: str, num2: str) -> str:
        # Method: Multiply strings by each single character
        # Time: O(m * n). Space: O(m+n)
        if num1 == '0' or num2 == '0':
            return '0'
        m, n = len(num1), len(num2)
        res = [0] * (m+n)
        for i in range(m-1, -1, -1):
            for j in range(n-1, -1, -1):
                p1, p2 = i+j, i+j+1
                cur_mul = int(num1[i]) * int(num2[j])
                cur_sum = cur_mul + res[p2]
                res[p2] = cur_sum % 10
                res[p1] += cur_sum // 10
        # Ignore the prefix zero
        index = 0
        while index <= len(res)-1 and res[index] == 0:
            index += 1
        return ''.join(str(num) for num in res[index:])
