class Solution {
    int rev[];
    int tot;
    public String multiply(String s, String t) {
        if((s.length() == 1 && Integer.parseInt(s) == 0) || (t.length() == 1 && Integer.parseInt(t) == 0)) {
            return "0";
        }

        int n  = s.length() - 1;
        int m   = t.length() - 1;

        int N = 1000;
        Complex A[] = new Complex[N];
        Complex B[] = new Complex[N];
        rev = new int[N];
        Arrays.setAll(A, e->new Complex(0,0));
        Arrays.setAll(B, e->new Complex(0,0));

        for(int i = 0; i <= n; i++){
            A[i].x = s.charAt(n - i) - '0';
        }

        for(int i = 0; i <= m; i++){
            B[i].x = t.charAt(m - i) - '0';
        }
        int bit = 0;
        while ((1 << bit) < n + m + 1) bit ++;
        tot = 1 << bit;

        for (int i = 0; i < tot; i ++) {
            rev[i] = (rev[i >> 1] >> 1) | ((i & 1) << (bit - 1));
        }

        fft(A,1);
        fft(B,1);
        for(int i = 0; i < tot; i++) {
            A[i].mul1(B[i]);
        }
        fft(A, -1);

        StringBuilder str = new StringBuilder();
        int add = 0;
        for(int i = 0; i <= n + m || add != 0; i++){
            int x = ((int)(A[i].x / tot + 0.5));
            int d = (add + x) % 10;
            str.append(d+"");
            add = (add + x) / 10;
        }
        return str.reverse().toString();
    }

    public void fft(Complex a[], int inv){
        for (int i = 0; i < tot; i ++ ){
            if (i < rev[i]) {
                swap(a, i, rev[i]);
            }
        }
        for (int mid = 1; mid < tot; mid <<= 1) {
            Complex w1 = new Complex(Math.cos(Math.PI / mid), Math.sin(Math.PI / mid) * inv);
            for (int i = 0; i < tot; i += mid * 2) {
                Complex wk = new Complex(1, 0);
                for (int j = 0; j < mid; j ++, wk = wk.mul(w1)) {
                    Complex x = a[i + j], y = wk.mul(a[i + j + mid]);
                    a[i + j] = x.add(y);
                    a[i + j + mid] = x.sub(y);
                }
            }
        }
    }

    public void swap(Complex A[], int i, int j){
        Complex t = A[i];
        A[i] = A[j];
        A[j] = t;
    }

    class Complex{
        double x = 0.0;
        double y = 0.0;

        public Complex(double x, double y){
            this.x = x;
            this.y = y;
        }

        public Complex add(Complex other){
            return new Complex(this.x + other.x, this.y + other.y);
        }

        public Complex sub(Complex other){
            return new Complex(this.x - other.x, this.y - other.y);
        }

        public Complex mul(Complex other){
            return new Complex(this.x * other.x - this.y * other.y, this.x * other.y + this.y * other.x);
        }

        public void mul1(Complex other){
            double xx = this.x * other.x - this.y * other.y;
            double yy = this.x * other.y + this.y * other.x;
            this. x = xx;
            this. y = yy;
        }
    }
}
